import React, { useContext, useState } from "react";
import { Link } from "react-router-dom";
import { AuthContext } from "../context/authContext";
import Logo from "../img/logo.png";
import Modal from "react-modal";
import Write from "../pages/Write";

Modal.setAppElement("#root"); // Set the root element for modal accessibility

const Navbar = () => {
  const { currentUser, logout } = useContext(AuthContext);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState("");

  const openModal = () => {
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  const handleSearchChange = (event) => {
    setSearchQuery(event.target.value);
    // Handle search logic here
  };

  return (
    <div className="navbar">
      <div className="container">
        <div className="logo">
          <Link to="/" style={{ cursor: "pointer" }}>
            <img src={Logo} alt="" />
          </Link>
        </div>
        <div className="search-bar">
          <input
            type="text"
            placeholder="Search..."
            value={searchQuery}
            onChange={handleSearchChange}
          />
          <button>Search</button>
        </div>
        <div className="links">
          <Link
            to={`/profile/${currentUser?.username}`}
            style={{ textDecoration: "none", color: "inherit" }}
          >
            <span>{currentUser?.username}</span>
          </Link>
          {currentUser ? (
            <span onClick={logout}>Logout</span>
          ) : (
            <Link className="link" to="/login">
              Login
            </Link>
          )}
          <span className="write">
            <button className="link" onClick={openModal}>
              Write
            </button>
          </span>
        </div>
      </div>
      <Modal
        isOpen={isModalOpen}
        onRequestClose={closeModal}
        contentLabel="Write Modal"
        style={{
          content: {
            width: "40%",
            height: "40%",
            margin: "auto",
            padding: "20px", // Add padding to the modal content
            borderRadius: "8px",
            overflow: "auto",
            boxShadow: "0 4px 8px rgba(0, 0, 0, 0.2)",
          },
          overlay: {
            backgroundColor: "rgba(0, 0, 0, 0.5)", // Add overlay background color
          },
        }}
      >
        <button
          style={{
            position: "absolute",
            top: "10px", // Adjust top position
            right: "10px", // Adjust right position
            background: "transparent",
            border: "none",
            cursor: "pointer",
            fontSize: "20px",
            color: "#555",
          }}
          onClick={closeModal}
        >
          X
        </button>{" "}
        {/* Close button */}
        <Write closeModal={closeModal} />
      </Modal>
    </div>
  );
};

export default Navbar;
