import React, { useEffect, useState } from "react";
import Edit from "../img/edit.png";
import Delete from "../img/delete.png";
import { Link, useLocation, useNavigate } from "react-router-dom";
import Menu from "../components/Menu";
import axios from "axios";
import moment from "moment";
import { useContext } from "react";
import { AuthContext } from "../context/authContext";
import DOMPurify from "dompurify";

const Single = () => {
  const [posts, setPosts] = useState([]);

  const location = useLocation();
  const navigate = useNavigate();

  const username = location.pathname.split("/")[2];

  const { currentUser } = useContext(AuthContext);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await axios.get(`/posts/${username}`);
        setPosts(res.data);
      } catch (err) {
        console.log(err);
      }
    };
    fetchData();
  }, [username]);

  const handleDelete = async (postId) => {
    try {
      await axios.delete(`/posts/${postId}`);
      navigate("/");
    } catch (err) {
      console.log(err);
    }
  };

  const getText = (html) => {
    const doc = new DOMParser().parseFromString(html, "text/html");
    return doc.body.textContent;
  };

  return (
    <div className="single" style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'space-between' }}>
      {posts.map((post) => (
        <div key={post.id} className="content" style={{ width: 'calc(33.33% - 10px)', marginBottom: '20px' }}>
          <img src={`../upload/${post.img}`} alt="" />
          <div className="user">
            {post.userImg && <img src={post.userImg} alt="" />}
            <div className="info">
              <span>{post.username}</span>
              <p>Posted {moment(post.date).fromNow()}</p>
            </div>
            {currentUser.username === post.username && (
              <div className="edit">
                <Link to={`/write?edit=${post.id}`} state={post}>
                  <img src={Edit} alt="" />
                </Link>
                <img onClick={() => handleDelete(post.id)} src={Delete} alt="" />
              </div>
            )}
          </div>
          {/* <h1>{post.title}</h1> */}
          <p
            dangerouslySetInnerHTML={{
              __html: DOMPurify.sanitize(post.description),
            }}
          ></p>
        </div>
      ))}
      {/* <Menu cat={post.cat}/> */}
    </div>
  );
};

export default Single;
