import React, { useContext, useState } from "react";
import { Link } from "react-router-dom";
import { AuthContext } from "../context/authContext";
import Logo from "../img/logo.png";
import Modal from "react-modal";
import Write from "../pages/Write";

Modal.setAppElement("#root"); // Set the root element for modal accessibility

const Navbar = () => {
  const { currentUser, logout } = useContext(AuthContext);
  const [isModalOpen, setIsModalOpen] = useState(false);

  const openModal = () => {
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  const customStyles = {
    content: {
      width: "40%",
      height: "40%",
      margin: "auto",
      borderRadius: "8px",
      overflow: "auto",
      boxShadow: "0 4px 8px rgba(0, 0, 0, 0.2)",
    },

  };

  return (
    <div className="navbar">
      <div className="container">
        <div className="logo">
          <Link to="/">
            <img src={Logo} alt="" />
          </Link>
        </div>
        <div className="links">
          <Link className="link" to="/feed">
            <h6>FEED</h6>
          </Link>
          <span>{currentUser?.username}</span>
          {currentUser ? (
            <span onClick={logout}>Logout</span>
          ) : (
            <Link className="link" to="/login">
              Login
            </Link>
          )}
          <span className="write">
            <button style={{"padding":"5px"}} className="link" onClick={openModal}>
              Write
            </button>
          </span>
        </div>
      </div>
      <Modal
        isOpen={isModalOpen}
        onRequestClose={closeModal}
        contentLabel="Write Modal"
        style={customStyles} // Apply custom styles to the modal
      >
        <button onClick={closeModal}>X</button>
        <Write />
      </Modal>
    </div>
  );
};

export default Navbar;
