import axios from "axios";
import React, { useState } from "react";
import { useContext } from "react";
import { Link, useNavigate } from "react-router-dom";
import { AuthContext } from "../context/authContext";
import {GoogleLogin} from 'react-google-login';
const Login = () => {
  const [inputs, setInputs] = useState({
    username: "",
    password: "",
  });
  const [err, setError] = useState(null);

  const navigate = useNavigate();

  const { login } = useContext(AuthContext);
  const googlesuccess = async (res) => {
    console.log(res);
    // Check if the response contains an error indicating that the pop-up was closed by the user
    if (res.error && res.error === 'popup_closed_by_user') {
      // Handle the case where the pop-up was closed by the user
      console.log("Popup was closed by the user.");
      // Display a message to the user or take appropriate action
    } else {
      // Handle the successful authentication process
      console.log("Authentication successful.");
      // Proceed with any necessary actions, such as logging the user in
    }
  };

  const googlefailure = (error) =>{
    console.log(error);
    console.log("failed/unsuccessful")
  }

  const handleChange = (e) => {
    setInputs((prev) => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await login(inputs)
      navigate("/");
    } catch (err) {
      setError(err.response.data);
    }
  };
  return (
    <div className="auth">
      <h1>Login</h1>
      <form>
        <input
          required
          type="text"
          placeholder="username"
          name="username"
          onChange={handleChange}
        />
        <input
          required
          type="password"
          placeholder="password"
          name="password"
          onChange={handleChange}
        />
        <button onClick={handleSubmit}>Login</button>
        <GoogleLogin
          clientId="552604077034-4862ahh8lgkgcaanm7nu1tt6d0f6b388.apps.googleusercontent.com"
          render={(renderProps)=>(
            <button onClick={renderProps.onClick} disabled={renderProps.disabled}>Sign Up with google</button>
          )}
          onSuccess={googlesuccess}
          onFailure={googlefailure}
          cookiePolicy="single_host_origin"


        />




        {err && <p>{err}</p>}
        <span>
          Don't you have an account? <Link to="/register">Register</Link>
        </span>
      </form>
    </div>
  );
};

export default Login;
