import React, { useEffect, useState } from "react";
import Edit from "../img/edit.png";
import Delete from "../img/delete.png";
import { Link, useLocation, useNavigate } from "react-router-dom";
import Menu from "../components/Menu";
import axios from "axios";
import moment from "moment";
import { useContext } from "react";
import { AuthContext } from "../context/authContext";
import DOMPurify from "dompurify";

const Single = () => {
  const [posts, setPosts] = useState([]);

  const location = useLocation();
  const navigate = useNavigate();

  const username = location.pathname.split("/")[2];

  const { currentUser } = useContext(AuthContext);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await axios.get(`/posts/${username}`);
        setPosts(res.data);
      } catch (err) {
        console.log(err);
      }
    };
    fetchData();
  }, [username]);

  const handleDelete = async (postId) => {
    try {
      await axios.delete(`/posts/${postId}`);
      navigate("/");
    } catch (err) {
      console.log(err);
    }
  };

  const getText = (html) => {
    const doc = new DOMParser().parseFromString(html, "text/html");
    return doc.body.textContent;
  };

  return (
    <div className="posts-container">
      {/* Map over the posts array */}
      {posts.reduce((rows, post, index) => {
        if (index % 3 === 0) {
          // Start a new row
          rows.push(
            <div key={`row_${index}`} className="post-row">
              {/* Render up to 3 posts in this row */}
              {posts.slice(index, index + 3).map((post) => (
                <div key={post.id} className="post">
                  {/* Render the post content */}
                  <div className="content">
                    {/* Set a fixed width and height for the image */}
                    <img src={`../upload/${post.img}`} alt="" style={{ width: "200px", height: "200px" }} />
                    <div className="user">
                      {post.userImg && <img src={post.userImg} alt="" />}
                      <div className="info">
                        <span>{post.username}</span>
                        <p>Posted {moment(post.date).fromNow()}</p>
                      </div>
                      {currentUser.username === post.username && (
                        <div className="edit">
                          <Link to={`/write?edit=${post.id}`} state={post}>
                            <img src={Edit} alt="" />
                          </Link>
                          <img onClick={() => handleDelete(post.id)} src={Delete} alt="" />
                        </div>
                      )}
                    </div>
                    <p dangerouslySetInnerHTML={{ __html: DOMPurify.sanitize(post.description) }}></p>
                  </div>
                </div>
              ))}
            </div>
          );
        }
        return rows;
      }, [])}
    </div>
  );


};

export default Single;
